  1,//editable        $row->location,         ''//$attends      );    }	}catch(Exception $e){     $ret['error'] = $e->getMessage();      error_log("####DEBUG datafeed.php-listCalendarByRange,error=".$ret['error']);  }  error_log("####DEBUG datafeed.php-listCalendarByRange,AFTER=".$ret['error']);  return $ret;}function listCalendar($day, $type){   error_log("####DEBUG datafeed.php-listCalendar");  $phpTime = js2PhpTime($day);  //echo $phpTime . "+" . $type;  switch($type){    case "month":      $st = mktime(0, 0, 0, date("m", $phpTime), 1, date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime)+1, 1, date("Y", $phpTime));      break;    case "week":      //suppose first day of a week is monday       $monday  =  date("d", $phpTime) - date('N', $phpTime) + 1;      //echo date('N', $phpTime);      $st = mktime(0,0,0,date("m", $phpTime), $monday, date("Y", $phpTime));      $et = mktime(0,0,-1,date("m", $phpTime), $monday+7, date("Y", $phpTime));      break;    case "day":      $st = mktime(0, 0, 0, date("m", $phpTime), date("d", $phpTime), date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime), date("d", $phpTime)+1, date("Y", $phpTime));      break;  }  //echo $st . "--" . $et;  return listCalendarByRange($st, $et);}function updateCalendar($id, $st, $et){   error_log("####DEBUG datafeed.php-updateCalendar");  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "update jqcalendar set"      . " starttime='" . php2MySqlTime(js2PhpTime($st)) . "', "      . " endtime='" . php2MySqlTime(js2PhpTime($et)) . "' "      . "where id=" . $id;    //echo $sql;		if(pg_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = pg_last_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function updateDetailedCalendar($id, $st, $et, $sub, $ade, $dscr, $loc, $color, $tz){   error_log("####DEBUG datafeed.php-addDetailedCalendar");  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "update jqcalendar set"      . " starttime='" . php2MySqlTime(js2PhpTime($st)) . "', "      . " endtime='" . php2MySqlTime(js2PhpTime($et)) . "', "      . " subject='" . pg_escape_string($sub) . "', "      . " isalldayevent='" . pg_escape_string($ade) . "', "      . " description='" . pg_escape_string($dscr) . "', "      . " location='" . pg_escape_string($loc) . "', "      . " color='" . pg_escape_string($color) . "' "      . "where id=" . $id;    //echo $sql;error_log("####DEBUG datafeed.php-addDetailedCalendar sql=".$sql);		if(pg_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = pg_last_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function removeCalendar($id){   error_log("####DEBUG datafeed.php-removeCalendar");  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "delete from jqcalendar where id=" . $id;		if(pg_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = pg_last_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}header('Content-type:text/javascript;charset=UTF-8');$method = $_GET["method"];switch ($method) {    case "add":        $ret = addCalendar($_POST["CalendarStartTime"], $_POST["CalendarEndTime"], $_POST["CalendarTitle"], $_POST["IsAllDayEvent"]);        break;    case "list":        $ret = listCalendar($_POST["showdate"], $_POST["viewtype"]);        break;    case "update":        $ret = updateCalendar($_POST["calendarId"], $_POST["CalendarStartTime"], $_POST["CalendarEndTime"]);        break;     case "remove":        $ret = removeCalendar( $_POST["calendarId"]);        break;    case "adddetails":        $st = $_POST["stpartdate"] . " " . $_POST["stparttime"];        $et = $_POST["etpartdate"] . " " . $_POST["etparttime"];        if(isset($_GET["id"])){            $ret = updateDetailedCalendar($_GET["id"], $st, $et,                 $_POST["Subject"], isset($_POST["IsAllDayEvent"])?1:0, $_POST["Description"],                 $_POST["Location"], $_POST["colorvalue"], $_POST["timezone"]);        }else{            $ret = addDetailedCalendar($st, $et,                                    $_POST["Subject"], isset($_POST["IsAllDayEvent"])?1:0, $_POST["Description"],                 $_POST["Location"], $_POST["colorvalue"], $_POST["timezone"]);        }                break; }echo json_encode($ret); ?>